(function($){$.include={script:function(src)
{if($("script[@src='"+src+"']").size())
return;if(/complete|loaded/.test(document.readyState))
{var el=document.createElement("script");el.setAttribute("type","text/javascript");el.setAttribute("src",src);document.getElementsByTagName("body")[0].appendChild(el);}
else
{document.write("<script src=\""+src+"\" type=\"text/javascript\"></script>");}},css:function(src,media)
{if($("head link[@href='"+src+"']").size())
return;var el=document.createElement("link");el.setAttribute("type","text/css");el.setAttribute("rel","stylesheet");el.setAttribute("href",src);el.setAttribute("media",media||"screen");$("head:first")[0].appendChild(el);},image:function()
{for(var i=0;i<arguments.length;++i)
$("<img>").attr("src",arguments[i]);}};})(jQuery);
